// Extract valid prompt pairs from spreadsheet row
const row = $input.first().json;
const promptPairs = [];

// Check up to 16 pairs
for (let i = 1; i <= 16; i++) {
  const imagePrompt = row['image_prompt' + i];
  const videoPrompt = row['video_prompt' + i];

  // Only add if both prompts exist and are not empty
  if (imagePrompt && imagePrompt.trim() && videoPrompt && videoPrompt.trim()) {
    promptPairs.push({
      index: i,
      imagePrompt: imagePrompt.trim(),
      videoPrompt: videoPrompt.trim(),
      isFirst: promptPairs.length === 0,
      isLast: false
    });
  }
}

if (promptPairs.length === 0) {
  throw new Error('No valid prompt pairs found');
}

// Mark the last pair
promptPairs[promptPairs.length - 1].isLast = true;

return [{
  json: {
    rowNumber: row.row_number || row.$rowIndex + 2,
    promptPairs: promptPairs,
    totalScenes: promptPairs.length,
    originalRow: row
  }
}];

// Parse image generation result
const output = $input.first().json.stdout;
let result;

try {
  // Find JSON in output (may have console.error messages before it)
  const jsonMatch = output.match(/\{[^{}]*"success"[^{}]*\}/);
  if (jsonMatch) {
    result = JSON.parse(jsonMatch[0]);
  } else {
    result = JSON.parse(output);
  }
} catch (e) {
  throw new Error('Failed to parse image result: ' + output);
}

if (!result.success) {
  throw new Error('Image generation failed: ' + (result.error || 'Unknown error'));
}

const prevData = $('Extract Prompt Pairs').item.json;

return [{
  json: {
    projectUrl: result.projectUrl,
    imagePath: result.outputPath,
    promptPairs: prevData.promptPairs,
    totalScenes: prevData.totalScenes,
    rowNumber: prevData.rowNumber
  }
}];

// Parse video result and prepare for loop or completion
const output = $input.first().json.stdout;
let result;

try {
  const jsonMatch = output.match(/\{[^{}]*"success"[^{}]*\}/);
  if (jsonMatch) {
    result = JSON.parse(jsonMatch[0]);
  } else {
    result = JSON.parse(output);
  }
} catch (e) {
  throw new Error('Failed to parse video result: ' + output);
}

if (!result.success) {
  throw new Error('Video generation failed: ' + (result.error || 'Unknown error'));
}

const prevData = $('Parse First Image Result').item.json;
const promptPairs = prevData.promptPairs;

// If first scene was the only scene
if (promptPairs[0].isLast) {
  return [{
    json: {
      completed: true,
      outputPath: result.outputPath,
      projectUrl: result.projectUrl,
      totalScenes: 1,
      rowNumber: prevData.rowNumber
    }
  }];
}

// Prepare remaining scenes for loop
const remainingPairs = promptPairs.slice(1);
return remainingPairs.map((pair, idx) => ({
  json: {
    completed: false,
    imagePrompt: pair.imagePrompt,
    videoPrompt: pair.videoPrompt,
    isLast: pair.isLast,
    sceneIndex: pair.index,
    projectUrl: result.projectUrl,
    rowNumber: prevData.rowNumber,
    totalScenes: prevData.totalScenes
  }
}));

// Parse loop image result
const output = $input.first().json.stdout;
let result;

try {
  const jsonMatch = output.match(/\{[^{}]*"success"[^{}]*\}/);
  if (jsonMatch) {
    result = JSON.parse(jsonMatch[0]);
  } else {
    result = JSON.parse(output);
  }
} catch (e) {
  throw new Error('Failed to parse image result: ' + output);
}

if (!result.success) {
  throw new Error('Image generation failed: ' + (result.error || 'Unknown error'));
}

const prevItem = $('Loop Over Items').item.json;

return [{
  json: {
    videoPrompt: prevItem.videoPrompt,
    isLast: prevItem.isLast,
    sceneIndex: prevItem.sceneIndex,
    projectUrl: result.projectUrl,
    rowNumber: prevItem.rowNumber,
    totalScenes: prevItem.totalScenes
  }
}];

// Parse loop video result and check if done
const output = $input.first().json.stdout;
let result;

try {
  const jsonMatch = output.match(/\{[^{}]*"success"[^{}]*\}/);
  if (jsonMatch) {
    result = JSON.parse(jsonMatch[0]);
  } else {
    result = JSON.parse(output);
  }
} catch (e) {
  throw new Error('Failed to parse video result: ' + output);
}

if (!result.success) {
  throw new Error('Video generation failed: ' + (result.error || 'Unknown error'));
}

const prevItem = $('Parse Image (Loop)').item.json;

// If this was the last scene, go to upload
if (prevItem.isLast) {
  return [{
    json: {
      completed: true,
      outputPath: result.outputPath,
      projectUrl: result.projectUrl,
      sceneIndex: prevItem.sceneIndex,
      rowNumber: prevItem.rowNumber,
      totalScenes: prevItem.totalScenes
    }
  }];
}

// Not the last scene - continue loop
return [{
  json: {
    completed: false,
    projectUrl: result.projectUrl,
    sceneIndex: prevItem.sceneIndex,
    rowNumber: prevItem.rowNumber,
    totalScenes: prevItem.totalScenes
  }
}];

// Prepare data for file reading and upload
let rowNumber, totalScenes, projectUrl;

try {
  const loopData = $('Parse Video (Loop)').item?.json;
  if (loopData) {
    rowNumber = loopData.rowNumber;
    totalScenes = loopData.totalScenes;
    projectUrl = loopData.projectUrl;
  }
} catch (e) {
  try {
    const directData = $('Prepare Loop').item?.json;
    if (directData) {
      rowNumber = directData.rowNumber;
      totalScenes = directData.totalScenes;
      projectUrl = directData.projectUrl;
    }
  } catch (e2) {}
}

return [{
  json: {
    rowNumber: rowNumber,
    totalScenes: totalScenes,
    projectUrl: projectUrl,
    filePath: '/tmp/veo3_final.mp4'
  }
}];

// Get Drive URL from upload result
const fileId = $input.first().json.id;
const driveUrl = 'https://drive.google.com/file/d/' + fileId + '/view';

// Get previous data
const prevData = $('Read Video File').item.json;

return [{
  json: {
    driveUrl: driveUrl,
    fileId: fileId,
    rowNumber: prevData.rowNumber,
    totalScenes: prevData.totalScenes,
    projectUrl: prevData.projectUrl
  }
}];
